    public function ImageUpload2($dir, $file) {
      
        if ($file['error'] === UPLOAD_ERR_OK) {
            
            $id = CakeText::uuid();
            $img_name_arr = explode(".", $file['name']);
            $img_name = str_replace(" ", "", $img_name_arr[0]) . $id . "." . end($img_name_arr);
            if (!file_exists($dir)) {
                mkdir($dir, 0777, true);
            }
            if (!file_exists($dir . '/thumbs')) {
                mkdir($dir . '/thumbs', 0777, true);
            }
            $image_name = '';
            if (move_uploaded_file($file['tmp_name'], $dir . DS . $img_name)) {
                $input_file         = $dir. DS . $img_name;
                $output_file        = $dir. DS .'thumbs'. DS . $img_name;
                $status = ImageTool::resize(array(
                'input' => $input_file,
                'output' => $output_file,
                'width' => 300,
                'height' => 300
                )); 
                if($status) {
                    $image_name = $img_name;
                }
            }
            return $image_name;
        }
    }
    
    public function CsvUpload($dir, $file) {
        if (!file_exists($dir)) {
            mkdir($dir, 0777, true);
        }
        $file_name = '';
        if ($file['error'] === UPLOAD_ERR_OK) {
            $id = CakeText::uuid();
            $file_name_arr = explode(".", $file['name']);
            $csv_file_name = str_replace(" ", "", $file_name_arr[0]) . $id . "." . end($file_name_arr);
            if (move_uploaded_file($file['tmp_name'], $dir . DS . $csv_file_name)) {
                $file_name = $csv_file_name;
            }
            return $file_name;
        }
    }
    
    public function getYTVideos($BASEURL, $dir_vids, $vid) {
        
        if (!file_exists($dir_vids)) {
            mkdir($dir_vids, 0777, true);
        }
        
        $response = array();
        $response['status'] = 0;
        $response['msg'] = "";
        $response['video'] = '';
        $response['title'] = '';
        if (file_exists($dir_vids . $vid . ".mp4") == FALSE) {
           
            $vformat = 'video/mp4';
           
            parse_str(file_get_contents("http://youtube.com/get_video_info?video_id=" . $vid), $info);
            echo "<pre>";
            print_r($info);
            exit;
            if (!isset($info['errorcode'])) {
                
                //$title = $this->removeEmoji($info['title']);
                
                $player_response = $info['player_response'];
                $player_arr = json_decode($player_response, TRUE);
                
                $title = $this->removeEmoji($player_arr['videoDetails']['title']);
                $streams = $info['url_encoded_fmt_stream_map'];
                $streams = ($streams != '') ? explode(',', $streams) : '';
                if(count($streams) > 0) {
                    foreach ($streams as $stream) {
                        parse_str($stream, $data);
                        if (stripos($data['type'], $vformat) !== false) {
                            //video
                            $vid_name = $vid . '.mp4';
                            $file_headers = @get_headers($data['url']);
                            if ($file_headers[0] == 'HTTP/1.1 200 OK') {
                                if (($video = fopen($data['url'], "r")) !== false) {
                                    //                        $video = fopen($data['url'], 'r');
                                    $file = fopen($dir_vids . $vid_name, 'w');
                                    stream_copy_to_stream($video, $file);
                                    fclose($video);
                                    fclose($file);
                                    //thumb
                                    $thmb_name = $vid_name . '.jpg';
                                    $t_src = "https://i.ytimg.com/vi/{$vid}/hqdefault.jpg";
                                    copy($t_src, $dir_vids . $thmb_name);
                                    $response['status'] = 1;
                                    $response['msg'] = "success";
                                    $response['video'] = $BASEURL . $vid_name;
                                    $response['title'] = $title.".mp4";
                                } else {
                                    $response['msg'] = "failed to open file";
                                }
                            } else {
                                $response['msg'] = "failed to open url";
                            }
                        }
                    }
                
                }else { $response['msg'] = "failed to open url"; }
            }
        } else {
            $response['msg'] = "this video already exist";
        }
        return $response;
    }
    
